import Axios from 'axios'
export const setUser = () => {
	const token = localStorage.getItem('userAuthToken') 

	return async (dispatch, getState) => {

		const url = "https://keepnotesec.herokuapp.com/api//users/account"
		try {
			const response = await Axios({
            	method: 'post',
            	url: url,
            	data: {},
            	headers: {"x-auth": token}
        	})

		    const responseData = await response.data
		    dispatch({type: 'SET_USER',payload: responseData})
		}catch(error){
			console.log("ERROR", error)
		}
	}
}

export const login = (formData) => {
	return async (dispatch, getState) => {
		try {
	        let url = "https://keepnotesec.herokuapp.com/api/users/login"

			let response = await Axios({
            	url: url,
            	method: 'POST',
            	data: formData
        	})

        	response = await response.json()

        	if(response.data.errors){
				console.log("user logged errors", response.data.errors)
				throw new response.data.errors
	        }else{
				localStorage.setItem('userAuthToken', response.data.token)
	        }

		}catch(error){
			console.log('LOGIN ERROR', error)
		}
	}
}
