import React, {useContext, useState} from 'react'
import Axios from 'axios'
import PulseLoader from "react-spinners/PulseLoader";
import { css } from "@emotion/core";
import { connect } from 'react-redux'
import {useDispatch} from 'react-redux'

import {VALIDATOR_REQUIRE, VALIDATOR_EMAIL, VALIDATOR_MIN} from '../../UTIL/validators'
import {useForm} from '../../CUSTOM_HOOKS/formHook'

import Card from '../../UI/Card'
import Button from '../../UI/Button'
import Input from '../../UI/Input'

import classes from '../../CSS/Auth.module.css'

// actions
import {login} from '../../REDUX_STORE/ACTIONS/UserAction'

import { AuthContext } from '../../CONTEXTS/AuthContext'

const Auth = (props) => {
	const Auth = useContext(AuthContext)

	const override = css`
	  display: block;
	  margin: 0 auto;
	  color: yellow;
	`;

	const dispatch = useDispatch()
	const [isLoading, setIsLoading] = useState(false)
	const [isInvalidLogin, setInvalidLogin] = useState(false)
	const [errorText, setError] = useState()
	const [formState, inputHandler] = useForm({
            email: {
                value: '',
                isValid: false
            },
            password: {
                value: '',
                isValid: false
            }
        }, false)

	const sumbmitHandler = async (e) => {
		e.preventDefault()

		let formData = {
            email: formState.inputs.email.value,
            password: formState.inputs.password.value
        }

        try{
        	setIsLoading(true)
    	    const {email, password} = formData
	        await dispatch(login(email, password))
	        setIsLoading(false)
	        Auth.login()
	        props.history.push('/contests')
        }catch(error){
        	console.log("LOGIN ERRORS", error)
        	setInvalidLogin(true)
        	setError(error)
        	setIsLoading(false)
        }


		// Axios({
  //           url: url,
  //           method: 'POST',
  //           data: formData
  //       }).then (response => {
  //       	if(response.data.errors){
		// 		console.log("user logged errors", response.data.errors)
		// 		setIsLoading(false)
		// 		setInvalidLogin(true)
		// 		setTimeout(() => {
		// 			setInvalidLogin(false)
		// 		}, 3000)
	 //        }else{
		// 		localStorage.setItem('userAuthToken', response.data.token)

		// 		dispatch(setUser())
	 //            setTimeout(() => {
		// 			setIsLoading(false)
		// 			Auth.login()
	 //        	    props.history.push('/contests')
		// 		},3000)
	 //        }
  //       }).catch(error => {
  //       	console.log("LOGIN CATCH ERROR", error)
  //       })
	}

	return (
		<div className = {classes.Authentication}>
			<Card>
				{isInvalidLogin && <p>{errorText}</p>}
				<form onSubmit = {sumbmitHandler} className = {classes.LoginForm}>
					<Input
	                	inputtype = "input" 
	                    type = "text"
	                    id = "email"
	                    placeholder = "Enter email"
	                    label = "Email"
	                    onInput = {inputHandler}
	                    errortext = "Please enter valid email."
	                    validators = {[VALIDATOR_REQUIRE(), VALIDATOR_EMAIL()]}
	                />

	                <Input 
	                	inputtype = "input"
	                    type = "password"
	                    id = "password"
	                    placeholder = "Enter password"
	                    element = "input"
	                    label = "Password"
	                    onInput = {inputHandler}
	                    errortext = "Please enter valid password."
	                    validators = {[VALIDATOR_REQUIRE(), VALIDATOR_MIN(6)]}
	                />

	                <Button 
	                	type = "submit"
	                	size = "Small"
	                	disabled = {!formState.formIsValid}
	                >
	                	{isLoading ? <PulseLoader color="white" loading={isLoading} size={5} css={override}/> : 'Login'}
	                </Button>

				</form>
			</Card>
		</div>
	)
}

const mapStateToProps = (state) => {
    return {user: state.user}
}

export default connect(mapStateToProps)(Auth);
