const initialState = {
	user: {}
}

const user = (state = initialState, action) => {
	switch(action.type){
		case 'SET_USER' :
			// console.log("USER IS ", action)
			return {...state, user: action.payload.user}
		default:
			return state
	}
}

export default user;
