const initialState = {
	user: {}
}

const user = (state = initialState, action) => {
	switch(action.type){
		case 'SET_USER' :{
		    console.log("response DATA", action)
			return {...state, ...action.payload}			
		}
		default:
			return state
	}
}

export default user;
