import React, {Component} from 'react'
import DataTable, {createTheme} from 'react-data-table-component';

createTheme('solarized', {
  text: {
    primary: '#268bd2',
    secondary: '#2aa198',
  },
  background: {
    default: '#002b36',
  },
  context: {
    background: '#cb4b16',
    text: '#FFFFFF',
  },
  divider: {
    default: '#073642',
  },
  action: {
    button: 'rgba(0,0,0,.54)',
    hover: 'rgba(0,0,0,.08)',
    disabled: 'rgba(0,0,0,.12)',
  },
});

const columns = [
  {
    name: 'Title',
    selector: 'title',
    sortable: true,
  },
  {
    name: 'Game Id',
    selector: 'game_id',
    sortable: true,
    right: true,
  },
  {
    name: 'No of Winners',
    selector: 'winners',
    sortable: true,
    right: true,
  },
  {
    name: 'Status',
    selector: 'status',
    sortable: true,
    right: true,
  },
  {
  	name: 'Winners Declaration Date',
  	selector: 'date',
    sortable: true,
    right: true,
  }
];


class MyComponent extends Component {

  render() {
    return (
      <DataTable
        title="All Contests"
        columns={columns}
        data={this.props.data}
        striped
        highlightOnHover
        pointerOnHover
        responsive
        pagination
        fixedHeader
        subHeaderAlign='left'
      	    theme="solarized"

      />
    )
  }
};

export default MyComponent
