import {AUTHENTICATION, LOGOUT} from '../ACTIONS/UserAction'

const initialState = {
	token: null,
	userId: null
}

export default (state = initialState, action) => {
	console.log("authreducer", action)
	switch(action.type){
		case AUTHENTICATION: 
			return {
				token: action.token,
				userId: action.userId
			}
		case LOGOUT: 
			return initialState
		default: 
		return state;
	}
}
