import React, {Component} from 'react'
import DataTable from 'react-data-table-component';

// const data = [{ id: 1, title: 'Conan the Barbarian', year: '1990'},{ id: 2, title: 'Conan the Barbarian', year: '1982' }];
const columns = [
  {
    name: 'Title',
    selector: 'title',
    sortable: true,
  },
  {
    name: 'Game Id',
    selector: 'game_id',
    sortable: true,
    right: true,
  },
  {
    name: 'No of Winners',
    selector: 'winners',
    sortable: true,
    right: true,
  },
  {
    name: 'Status',
    selector: 'status',
    sortable: true,
    right: true,
  },
];


class MyComponent extends Component {

  render() {
  	const data = this.props.data.map((contest, id) => {
  		return (
  			{title: contest.title, game_id: contest.contest_unique_id, id, winners: contest.no_of_winners, status: contest.title}
  		)
  	})

    return (
      <DataTable
        title="All Contests"
        columns={columns}
        data={data}
      />
    )
  }
};

export default MyComponent
