import React, {useState, useContext} from 'react'

import {VALIDATOR_REQUIRE, VALIDATOR_EMAIL, VALIDATOR_MIN} from '../../UTIL/validators'
import {useForm} from '../../CUSTOM_HOOKS/formHook'

import Card from '../../UI/Card'
import Button from '../../UI/Button'
import Input from '../../UI/Input'

import classes from '../../CSS/Auth.module.css'
const Auth = () => {

	const [isLoading, setIsLoading] = useState(false)
	const [formState, inputHandler, setFormData] = useForm({
            email: {
                value: '',
                isValid: false
            },
            password: {
                value: '',
                isValid: false
            }
        }, false)

	const sumbmitHandler = (e) => {
		e.preventDefault()
	}

	return (
		<div className = {classes.Authentication}>
			<Card>
				<form onSubmit = {sumbmitHandler} className = {classes.LoginForm}>
					<Input
	                	inputtype = "input" 
	                    type = "text"
	                    id = "email"
	                    placeholder = "Enter email"
	                    label = "Email"
	                    onInput = {inputHandler}
	                    errortext = "Please enter valid email."
	                    validators = {[VALIDATOR_REQUIRE(), VALIDATOR_EMAIL()]}
	                />

	                <Input 
	                	inputtype = "input"
	                    type = "password"
	                    id = "password"
	                    placeholder = "Enter password"
	                    element = "input"
	                    label = "Password"
	                    onInput = {inputHandler}
	                    errortext = "Please enter valid password."
	                    validators = {[VALIDATOR_REQUIRE(), VALIDATOR_MIN(6)]}
	                />

	                <Button 
	                	type = "submit"
	                	size = "Small"
	                	disabled = {!formState.formIsValid}
	                >
	                	Login
	                </Button>

				</form>
			</Card>
		</div>
	)
}

export default Auth;
