import React, {useState, useContext} from 'react'
import Axios from 'axios'

import {VALIDATOR_REQUIRE, VALIDATOR_EMAIL, VALIDATOR_MIN} from '../../UTIL/validators'
import {useForm} from '../../CUSTOM_HOOKS/formHook'

import Card from '../../UI/Card'
import Button from '../../UI/Button'
import Input from '../../UI/Input'
import LoadingSpinner from '../../UI/LoadingSpinner'

import classes from '../../CSS/Auth.module.css'
const Auth = (props) => {

	const [isLoading, setIsLoading] = useState(false)
	const [formState, inputHandler] = useForm({
            email: {
                value: '',
                isValid: false
            },
            password: {
                value: '',
                isValid: false
            }
        }, false)

	const sumbmitHandler = (e) => {
		e.preventDefault()

		let formData = {
            email: formState.inputs.email.value,
            password: formState.inputs.password.value
        }

        let url = "https://keepnotesec.herokuapp.com/api/users/login"

        setIsLoading(true)

		Axios({
            url: url,
            method: 'POST',
            data: formData
        }).then (response => {
        	if(response.data.errors){
				console.log("user logged errors", response.data.errors)
				setIsLoading(false)
	        }else{
				console.log("LOGIN RESPONSE", response.data)
				localStorage.setItem('userAuthToken', response.data.token)
				// setIsLoading(false)
	            // props.history.push('/contests')
	        }
        }).catch(error => {
        	console.log("LOGIN CATCH ERROR", error)
        })
	}

	const fetchData = () => {
    	setIsLoading(true)

	    setTimeout(() => {
    	  this.setState({ loading: false });
    	}, 2000);
  	};

	console.log("IS LOADING", isLoading)

	return (
		<div className = {classes.Authentication}>
		<button className="button" onClick={fetchData} disabled={isLoading}>
          {isLoading && (
            <i
              className="fa fa-refresh fa-spin"
              style={{ marginRight: "5px" }}
            />
          )}
          {isLoading && <span>Loading Data from Server</span>}
          {!isLoading && <span>Fetch Data from Server</span>}
        </button>


			<Card>
				<form onSubmit = {sumbmitHandler} className = {classes.LoginForm}>
					<Input
	                	inputtype = "input" 
	                    type = "text"
	                    id = "email"
	                    placeholder = "Enter email"
	                    label = "Email"
	                    onInput = {inputHandler}
	                    errortext = "Please enter valid email."
	                    validators = {[VALIDATOR_REQUIRE(), VALIDATOR_EMAIL()]}
	                />

	                <Input 
	                	inputtype = "input"
	                    type = "password"
	                    id = "password"
	                    placeholder = "Enter password"
	                    element = "input"
	                    label = "Password"
	                    onInput = {inputHandler}
	                    errortext = "Please enter valid password."
	                    validators = {[VALIDATOR_REQUIRE(), VALIDATOR_MIN(6)]}
	                />

	                <Button 
	                	type = "submit"
	                	size = "Small"
	                	disabled = {!formState.formIsValid}
	                >
	                	Login
	                </Button>

				</form>
			</Card>
		</div>
	)
}

export default Auth;
